INSERT INTO `aa_addonarticle` ( `aid`, `typeid`, `body`, `redirecturl`, `templet`, `userip` ) VALUES( 1030, 8, '


<span style=\"font-size:14px;\">     手机<a href=\"http://live.aaniao.com/\"><strong>直播APP开发</strong></a>具有以下条件：<br/>
 <br/>
